import React, { useEffect, useState } from 'react';
import './Data.css';
import {Chart} from 'react-chartjs-2';

const Data = () => {
  const [data, setData] = useState('');
  const [wordFrequency, setWordFrequency] = useState({});
  const [chartData, setChartData] = useState(null);

  const fetchData = async () => {
    try {
      const response = await fetch('https://www.terriblytinytales.com/test.txt');
      const text = await response.text();
      setData(text);
      calculateWordFrequency(text);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    if (wordFrequency && Object.keys(wordFrequency).length > 0) {
      renderChart();
    }
  }, [wordFrequency]);

  useEffect(() => {
    fetchData();
  }, []);

  const calculateWordFrequency = (text) => {
    const words = text.split(/\s+/); // Split the text into an array of words
    const frequency = {};

    // Count the frequency of each word
    words.forEach((word) => {
      frequency[word] = (frequency[word] || 0) + 1;
    });

    setWordFrequency(frequency);
  };

  const renderChart = () => {
    const sortedWords = Object.entries(wordFrequency).sort((a, b) => b[1] - a[1]);
    const labels = sortedWords.slice(0, 20).map(([word]) => word);
    const counts = sortedWords.slice(0, 20).map(([_, count]) => count);

    const chartData = {
      labels,
      datasets: [
        {
          label: 'Word Frequency',
          data: counts,
          backgroundColor: 'rgba(75, 192, 192, 0.6)',
        },
      ],
    };

    const ctx = document.getElementById('chart');
    if (ctx) {
      if (chartData) {
        chartData.destroy(); // Destroy previous chart instance if exists
      }
      setChartData(
        new Chart(ctx.getContext('2d'), {
          type: 'bar',
          data: chartData,
          options: {
            responsive: true,
            maintainAspectRatio: false,
            scales: {
              y: {
                beginAtZero: true,
                stepSize: 1,
              },
            },
          },
        })
      );
    }
  };

  return (
    <div className="data">
      {data}
      <h2>Word Frequency:</h2>
      <table>
        <thead>
          <tr>
            <th>Word</th>
            <th>Frequency</th>
          </tr>
        </thead>
        <tbody>
          {Object.entries(wordFrequency).map(([word, count]) => (
            <tr key={word}>
              <td>{word}</td>
              <td>{count}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {chartData && (
        <Bar
          data={chartData}
          options={{
            responsive: true,
            maintainAspectRatio: false,
            scales: {
              y: {
                beginAtZero: true,
                stepSize: 1,
              },
            },
          }}
        />
      )}
    </div>
  );
};

export default Data;
