import React, { useEffect, useState, useRef } from 'react';
import './Data.css';
import { Bar } from 'react-chartjs-2';
import { Chart,LinearScale} from 'chart.js';

const Data = () => {
  const [data, setData] = useState('');
  const [wordFrequency, setWordFrequency] = useState({});
  const [chartData, setChartData] = useState(null);
  const chartDataInstance = useRef(null); // Add this line to declare chartDataInstance

  const fetchData = async () => {
    try {
      const response = await fetch('https://www.terriblytinytales.com/test.txt');
      const text = await response.text();
      setData(text);
      calculateWordFrequency(text);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    if (wordFrequency && Object.keys(wordFrequency).length > 0) {
      renderChart();
    }
  }, [wordFrequency]);

  useEffect(() => {
    fetchData();
  }, []);

  const calculateWordFrequency = (text) => {
    const words = text.split(/\s+/);
    const frequency = {};

    words.forEach((word) => {
      frequency[word] = (frequency[word] || 0) + 1;
    });

    setWordFrequency(frequency);
  };

  const renderChart = () => {
    const sortedWords = Object.entries(wordFrequency).sort((a, b) => b[1] - a[1]);
    const labels = sortedWords.slice(0, 20).map(([word]) => word);
    const counts = sortedWords.slice(0, 20).map(([_, count]) => count);

    const data = {
      labels,
      datasets: [
        {
          label: 'Word Frequency',
          data: counts,
          backgroundColor: 'rgba(75, 192, 192, 0.6)',
        },
      ],
    };
    Chart.register(LinearScale);
    const ctx = document.getElementById('chart');
    if (ctx) {
      if (chartDataInstance.current) {
        chartDataInstance.current.destroy();
      }
      chartDataInstance.current = new Chart(ctx.getContext('2d'), {
        type: 'bar',
        data,
        options: {
          responsive: true,
          maintainAspectRatio: false,
          scales: {
            y: {
              beginAtZero: true,
              stepSize: 1,
            },
          },
        },
      });
    }
  };

  return (
    <div className="data">
      {data}
      <h2>Word Frequency:</h2>

      <div className="chart-container" style={{ width: '100%', height: '400px' }}>
        <canvas id="chart" />
      </div>
    </div>
  );
};

export default Data;
